mapper.update:
UPDATE server_node AS rt SET expire_at = tt.expire_at, context_path = tt.context_path FROM (
SELECT #{item.expireAt} AS expire_at, #{item.contextPath} AS context_path, #{item.hostId} AS host_id, #{item.hostIp} AS host_ip
) AS tt WHERE rt.host_id = tt.host_id AND rt.host_ip = tt.host_ip

mapper.select:
SELECT node_type as nodeType, count(*) as total
from server_node
${ew.customSqlSegment}
