mapper.update:
UPDATE job_summary SET success_num = src.success_num, fail_num = src.fail_num, fail_reason = src.fail_reason, stop_num = src.stop_num, stop_reason = src.stop_reason, cancel_num = src.cancel_num, cancel_reason = src.cancel_reason FROM job_summary AS dest JOIN (
SELECT #{item.successNum} AS success_num, #{item.failNum} AS fail_num, #{item.failReason} AS fail_reason, #{item.stopNum} AS stop_num, #{item.stopReason} AS stop_reason, #{item.cancelNum} AS cancel_num, #{item.cancelReason} AS cancel_reason, #{item.triggerAt} AS trigger_at, #{item.businessId} AS business_id
) AS src ON (dest.trigger_at = src.trigger_at AND dest.business_id = src.business_id)
