mapper.update:
UPDATE retry_summary SET running_num = src.running_num, finish_num = src.finish_num, max_count_num = src.max_count_num, suspend_num = src.suspend_num FROM retry_summary AS dest JOIN (
SELECT #{item.runningNum} AS running_num, #{item.finishNum} AS finish_num, #{item.maxCountNum} AS max_count_num, #{item.suspendNum} AS suspend_num, #{item.triggerAt} AS trigger_at, #{item.sceneName} AS scene_name, #{item.namespaceId} AS namespace_id, #{item.groupName} AS group_name
) AS src ON (dest.trigger_at = src.trigger_at AND dest.group_name = src.group_name AND dest.scene_name = src.scene_name AND dest.namespace_id = src.namespace_id )
